import{initVideoModal}from"./videoModal.min.js";import{initSlideshow}from"./textSlideshow.min.js";const routerView=document.getElementById("routerView");const videoButtons=document.getElementsByTagName("button");const previewScreen=document.querySelector("#preview");export const routes={index:`/pages/about.html`,projects:`/pages/projects.html`,404:"/pages/404.html"};export async function navigate(route){const routePath=routes[route]||routes["404"];try{const response=await fetch(routePath);const routeContent=await response.text();routerView.classList.add("opacity-0");setTimeout((()=>{setTimeout((()=>{previewScreen.classList.add("opacity-0");setTimeout((()=>{previewScreen.remove()}),500)}),2500);routerView.innerHTML=routeContent;initVideoModal(videoButtons);if(route==="index"){initSlideshow()}routerView.classList.remove("opacity-0");routerView.classList.add("opacity-100")}),1e3)}catch(error){console.error("ERROR: ",error);navigate("404")}}export function handleRouteChange(){const path=window.location.pathname.substring(1)||"index";navigate(path)}