export class Starfield{constructor(canvas,starCount=50,oscillationSpeed=.002,oscillationAmplitude=3){this.canvas=canvas;this.ctx=canvas.getContext("2d");this.stars=[];this.starCount=starCount;this.oscillationSpeed=oscillationSpeed;this.oscillationAmplitude=oscillationAmplitude;this.resizeCanvas();this.createStars();this.drawStars()}resizeCanvas(){this.canvas.width=window.innerWidth;this.canvas.height=window.innerHeight}createStars(){this.stars.length=0;for(let i=0;i<this.starCount;i++){const x=Math.random()*this.canvas.width;const y=Math.random()*this.canvas.height;const size=Math.random()*2+.1;const phase=Math.random()*Math.PI*2;this.stars.push({x:x,y:y,size:size,phase:phase})}}drawStars(){this.ctx.clearRect(0,0,this.canvas.width,this.canvas.height);const time=Date.now()*this.oscillationSpeed;this.stars.forEach((star=>{const oscillation=Math.sin(time+star.phase)*this.oscillationAmplitude;this.ctx.beginPath();this.ctx.arc(star.x+oscillation,star.y+oscillation,star.size,0,Math.PI*2);this.ctx.fillStyle="white";this.ctx.fill()}));requestAnimationFrame((()=>this.drawStars()))}}