export function initSlideshow(){const slideTitle=document.getElementById("slide-title");const slideContent=document.getElementById("slide-content");const nextButton=document.getElementById("next-button");const currentSlideElement=document.getElementById("current-slide");const totalSlidesElement=document.getElementById("total-slides");const slideIndexElement=document.getElementById("slide-index");const imageSlideMain=document.getElementById("image-slide-main");const imageSlideSecondary=document.getElementById("image-slide-secondary");let currentSlideIndex=0;let slides=[];fetch("/assets/json/text.json").then((response=>response.json())).then((data=>{slides=data;totalSlidesElement.textContent=slides.length;updateSlide()})).catch((error=>console.error("Error loading text.json:",error)));function updateSlide(){slideTitle.classList.add("opacity-0");slideContent.classList.remove("opacity-80");slideContent.classList.add("opacity-0");slideIndexElement.classList.remove("opacity-50");slideIndexElement.classList.add("opacity-0");imageSlideMain.classList.add("opacity-0");imageSlideSecondary.classList.add("opacity-0");setTimeout((()=>{const slide=slides[currentSlideIndex];slideTitle.textContent=slide.title;slideContent.textContent=slide.content;imageSlideMain.src="./assets/images/imageForProj"+(currentSlideIndex+1)+".webp";imageSlideSecondary.src="./assets/images/imageForProj"+(currentSlideIndex+1)+".webp";currentSlideElement.textContent=currentSlideIndex+1;slideTitle.classList.remove("opacity-0");slideContent.classList.remove("opacity-0");slideContent.classList.add("opacity-80");slideIndexElement.classList.remove("opacity-0");slideIndexElement.classList.add("opacity-50");imageSlideMain.classList.remove("opacity-0");imageSlideSecondary.classList.remove("opacity-0")}),300)}nextButton.addEventListener("click",(()=>{currentSlideIndex=(currentSlideIndex+1)%slides.length;updateSlide()}))}